let select=document.querySelectorAll(".select"),selectHeader=document.querySelectorAll(".select__btn"),selectItem=document.querySelectorAll(".select__item");function openList(){let e=this.closest(".select");e.classList.contains("open")?e.classList.remove("open"):(select.forEach(e=>{e.classList.remove("open")}),e.classList.add("open"))}function chooseElement(){let e=this.innerText,t=this.closest(".select");t.querySelector(".select__current").innerText=e,t.classList.remove("open")}selectHeader.forEach(e=>{e.addEventListener("click",openList)}),selectItem.forEach(e=>{e.addEventListener("click",chooseElement)}),window.addEventListener("click",e=>{e.target.closest(".select")||select.forEach(e=>{e.classList.remove("open")})});